package Week1.day1;

public class IdentifyPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value = 876;
		if (value>0) {
			System.out.println("Given number is "+value+"positive");
		}
		else if (value<0) {
			System.out.println("Given value is negative");
			
		}
		else {
			System.out.println("Given number.."+value+" is not positive or negative number");
		}
		}

	}


